// Advanced XAU/USD Technical Analysis with ICT Concepts
try {
  const priceData = $input.first().json;

  if (!priceData?.chart?.result?.[0]) {
    return [{
      json: {
        error: 'Price data unavailable',
        signal: 'HOLD',
        confidence: 0,
        price: 'N/A'
      }
    }];
  }

  const chart = priceData.chart.result[0];
  const meta = chart.meta;
  const currentPrice = meta.regularMarketPrice || meta.previousClose;
  const previousClose = meta.previousClose;
  const change = previousClose ? ((currentPrice - previousClose) / previousClose * 100) : 0;

  // Get OHLCV data
  const quotes = chart.indicators?.quote?.[0] || {};
  const closes = quotes.close?.filter(price => price !== null).slice(-100) || [];
  const highs = quotes.high?.filter(price => price !== null).slice(-100) || [];
  const lows = quotes.low?.filter(price => price !== null).slice(-100) || [];
  const opens = quotes.open?.filter(price => price !== null).slice(-100) || [];
  const volumes = quotes.volume?.filter(vol => vol !== null).slice(-100) || [];

  // Advanced RSI with divergence detection
  function calculateAdvancedRSI(prices, period = 14) {
    if (prices.length < period + 1) return { rsi: 50, divergence: false };

    let gains = 0, losses = 0;

    for (let i = 1; i <= period; i++) {
      const diff = prices[i] - prices[i - 1];
      if (diff > 0) gains += diff;
      else losses += Math.abs(diff);
    }

    let avgGain = gains / period;
    let avgLoss = losses / period;

    for (let i = period + 1; i < prices.length; i++) {
      const diff = prices[i] - prices[i - 1];
      const gain = diff > 0 ? diff : 0;
      const loss = diff < 0 ? Math.abs(diff) : 0;

      avgGain = (avgGain * (period - 1) + gain) / period;
      avgLoss = (avgLoss * (period - 1) + loss) / period;
    }

    const rs = avgGain / avgLoss;
    const rsi = 100 - (100 / (1 + rs));

    // Check for divergence
    const recentPrices = prices.slice(-10);
    const priceDirection = recentPrices[recentPrices.length - 1] > recentPrices[0];
    const rsiDirection = rsi > 50;
    const divergence = priceDirection !== rsiDirection && Math.abs(change) > 0.5;

    return { rsi, divergence };
  }

  // ICT Order Blocks Detection
  function detectOrderBlocks(highs, lows, closes, volumes) {
    const orderBlocks = [];

    for (let i = 2; i < closes.length - 2; i++) {
      const currentVolume = volumes[i] || 0;
      const avgVolume = volumes.slice(Math.max(0, i-10), i).reduce((a, b) => a + b, 0) / 10;

      // Bullish Order Block
      if (currentVolume > avgVolume * 2 && 
          closes[i] > opens[i] && 
          closes[i+1] > highs[i]) {
        orderBlocks.push({
          type: 'bullish',
          level: highs[i],
          strength: currentVolume / avgVolume,
          index: i
        });
      }

      // Bearish Order Block
      if (currentVolume > avgVolume * 2 && 
          closes[i] < opens[i] && 
          closes[i+1] < lows[i]) {
        orderBlocks.push({
          type: 'bearish',
          level: lows[i],
          strength: currentVolume / avgVolume,
          index: i
        });
      }
    }

    return orderBlocks.slice(-5);
  }

  // Fair Value Gaps Detection
  function detectFairValueGaps(highs, lows) {
    const fvgs = [];

    for (let i = 1; i < highs.length - 1; i++) {
      // Bullish FVG
      if (lows[i-1] > highs[i+1]) {
        fvgs.push({
          type: 'bullish',
          upper: lows[i-1],
          lower: highs[i+1],
          index: i
        });
      }

      // Bearish FVG
      if (highs[i-1] < lows[i+1]) {
        fvgs.push({
          type: 'bearish',
          upper: lows[i+1],
          lower: highs[i-1],
          index: i
        });
      }
    }

    return fvgs.slice(-3);
  }

  // Volume Profile Analysis
  function calculateVolumeProfile(prices, volumes, bins = 20) {
    if (prices.length === 0 || volumes.length === 0) return { poc: 0, valueArea: { high: 0, low: 0 } };

    const priceRange = Math.max(...prices) - Math.min(...prices);
    const binSize = priceRange / bins;
    const profile = new Array(bins).fill(0);

    for (let i = 0; i < prices.length; i++) {
      const binIndex = Math.floor((prices[i] - Math.min(...prices)) / binSize);
      if (binIndex >= 0 && binIndex < bins) {
        profile[binIndex] += volumes[i] || 0;
      }
    }

    // Find Point of Control
    const maxVolumeIndex = profile.indexOf(Math.max(...profile));
    const poc = Math.min(...prices) + (maxVolumeIndex * binSize);

    // Calculate Value Area (70% of volume)
    const totalVolume = profile.reduce((a, b) => a + b, 0);
    const targetVolume = totalVolume * 0.7;
    let accumulatedVolume = profile[maxVolumeIndex];
    let upperIndex = maxVolumeIndex;
    let lowerIndex = maxVolumeIndex;

    while (accumulatedVolume < targetVolume && (upperIndex < bins - 1 || lowerIndex > 0)) {
      if (upperIndex < bins - 1 && (lowerIndex === 0 || profile[upperIndex + 1] > profile[lowerIndex - 1])) {
        upperIndex++;
        accumulatedVolume += profile[upperIndex];
      } else if (lowerIndex > 0) {
        lowerIndex--;
        accumulatedVolume += profile[lowerIndex];
      }
    }

    return {
      poc: poc.toFixed(2),
      valueArea: {
        high: (Math.min(...prices) + (upperIndex * binSize)).toFixed(2),
        low: (Math.min(...prices) + (lowerIndex * binSize)).toFixed(2)
      }
    };
  }

  // MACD Calculation
  function calculateMACD(prices) {
    if (prices.length < 26) return { macd: 0, signal: 0, histogram: 0 };

    function calculateEMA(data, period) {
      const multiplier = 2 / (period + 1);
      let ema = data[0];

      for (let i = 1; i < data.length; i++) {
        ema = (data[i] * multiplier) + (ema * (1 - multiplier));
      }
      return ema;
    }

    const ema12 = calculateEMA(prices.slice(-12), 12);
    const ema26 = calculateEMA(prices.slice(-26), 26);
    const macd = ema12 - ema26;
    const signal = macd * 0.9;
    const histogram = macd - signal;

    return { macd, signal, histogram };
  }

  // Calculate all indicators
  const rsiData = calculateAdvancedRSI(closes);
  const orderBlocks = detectOrderBlocks(highs, lows, closes, volumes);
  const fairValueGaps = detectFairValueGaps(highs, lows);
  const volumeProfile = calculateVolumeProfile(closes, volumes);
  const macdData = calculateMACD(closes);

  // Advanced signal generation
  let signal = 'HOLD';
  let confidence = 50;
  let reasoning = [];

  // RSI Analysis
  if (rsiData.rsi < 30) {
    reasoning.push('RSI oversold condition');
    confidence += 20;
    if (change > 0) {
      signal = 'BUY';
      confidence += 15;
    }
  } else if (rsiData.rsi > 70) {
    reasoning.push('RSI overbought condition');
    confidence += 20;
    if (change < 0) {
      signal = 'SELL';
      confidence += 15;
    }
  }

  if (rsiData.divergence) {
    reasoning.push('RSI divergence detected');
    confidence += 10;
  }

  // MACD Analysis
  if (macdData.macd > macdData.signal && macdData.histogram > 0) {
    reasoning.push('MACD bullish crossover');
    if (signal === 'HOLD') signal = 'WEAK_BUY';
    confidence += 15;
  } else if (macdData.macd < macdData.signal && macdData.histogram < 0) {
    reasoning.push('MACD bearish crossover');
    if (signal === 'HOLD') signal = 'WEAK_SELL';
    confidence += 15;
  }

  // Order Blocks Analysis
  const nearOrderBlock = orderBlocks.find(ob => 
    Math.abs(currentPrice - ob.level) / currentPrice < 0.002
  );

  if (nearOrderBlock) {
    reasoning.push(`Near ${nearOrderBlock.type} order block`);
    confidence += nearOrderBlock.strength * 5;
  }

  // Fair Value Gap Analysis
  const activeFVG = fairValueGaps.find(fvg => 
    currentPrice >= Math.min(fvg.upper, fvg.lower) && 
    currentPrice <= Math.max(fvg.upper, fvg.lower)
  );

  if (activeFVG) {
    reasoning.push(`Trading in ${activeFVG.type} FVG`);
    confidence += 8;
  }

  // Volume Profile Analysis
  const distanceFromPOC = Math.abs(currentPrice - parseFloat(volumeProfile.poc)) / currentPrice;
  if (distanceFromPOC > 0.01) {
    reasoning.push('Price away from POC');
    confidence += 10;
  }

  // Price momentum
  if (Math.abs(change) > 1) {
    reasoning.push(`Strong movement: ${change.toFixed(2)}%`);
    confidence += 10;
  }

  // Limit confidence
  confidence = Math.min(Math.max(confidence, 10), 95);

  // Risk Management Calculations
  const stopLoss = signal.includes('BUY') ? 
    currentPrice * 0.995 : currentPrice * 1.005;
  const takeProfit = signal.includes('BUY') ? 
    currentPrice * 1.015 : currentPrice * 0.985;

  const riskReward = Math.abs(takeProfit - currentPrice) / Math.abs(currentPrice - stopLoss);

  const signalEmoji = {
    'BUY': 'üöÄ',
    'WEAK_BUY': 'üìà',
    'HOLD': '‚è∏Ô∏è',
    'WEAK_SELL': 'üìâ',
    'SELL': 'üí•'
  };

  return [{
    json: {
      price: currentPrice.toFixed(2),
      change: change.toFixed(2),
      rsi: rsiData.rsi.toFixed(1),
      rsiDivergence: rsiData.divergence,
      macd: macdData.macd.toFixed(4),
      macdSignal: macdData.signal.toFixed(4),
      signal: signal,
      confidence: Math.round(confidence),
      reasoning: reasoning.join(', '),
      emoji: signalEmoji[signal] || '‚ö™',

      // ICT Analysis
      orderBlocks: orderBlocks.length,
      fairValueGaps: fairValueGaps.length,
      nearOrderBlock: nearOrderBlock ? `${nearOrderBlock.type} at ${nearOrderBlock.level.toFixed(2)}` : 'None',
      activeFVG: activeFVG ? `${activeFVG.type} FVG` : 'None',

      // Volume Profile
      volumeProfile: {
        poc: volumeProfile.poc,
        valueAreaHigh: volumeProfile.valueArea.high,
        valueAreaLow: volumeProfile.valueArea.low
      },

      // Risk Management
      stopLoss: stopLoss.toFixed(2),
      takeProfit: takeProfit.toFixed(2),
      riskReward: riskReward.toFixed(2),

      timestamp: new Date().toISOString()
    }
  }];

} catch (error) {
  console.error('Advanced analysis error:', error);
  return [{
    json: {
      error: 'Advanced analysis failed: ' + error.message,
      signal: 'HOLD',
      confidence: 0,
      price: 'Error',
      timestamp: new Date().toISOString()
    }
  }];
}

