// AI Ensemble Learning System
try {
  const technicalData = $input.first().json;
  const openaiData = $input.all().find(item => item.json?.choices);
  const cohereData = $input.all().find(item => item.json?.generations);
  const huggingfaceData = $input.all().find(item => item.json?.generated_text);

  // Initialize ensemble weights
  const weights = {
    technical: 0.35,
    openai: 0.30,
    cohere: 0.20,
    huggingface: 0.15
  };

  // Extract signals from each model
  let signals = {
    technical: technicalData.signal || 'HOLD',
    openai: 'HOLD',
    cohere: 'HOLD',
    huggingface: 'HOLD'
  };

  let confidenceScores = {
    technical: technicalData.confidence || 50,
    openai: 50,
    cohere: 50,
    huggingface: 50
  };

  // Process OpenAI analysis
  if (openaiData?.json?.choices?.[0]?.message?.content) {
    const analysis = openaiData.json.choices[0].message.content.toLowerCase();
    if (analysis.includes('buy') || analysis.includes('bullish')) {
      signals.openai = 'BUY';
      confidenceScores.openai = 75;
    } else if (analysis.includes('sell') || analysis.includes('bearish')) {
      signals.openai = 'SELL';
      confidenceScores.openai = 75;
    }
  }

  // Process Cohere sentiment
  if (cohereData?.json?.generations?.[0]?.text) {
    const sentiment = cohereData.json.generations[0].text.toLowerCase();
    if (sentiment.includes('bullish') || sentiment.includes('positive')) {
      signals.cohere = 'BUY';
      confidenceScores.cohere = 70;
    } else if (sentiment.includes('bearish') || sentiment.includes('negative')) {
      signals.cohere = 'SELL';
      confidenceScores.cohere = 70;
    }
  }

  // Process HuggingFace pattern
  if (huggingfaceData?.json?.generated_text) {
    const pattern = huggingfaceData.json.generated_text.toLowerCase();
    if (pattern.includes('upward') || pattern.includes('rise')) {
      signals.huggingface = 'BUY';
      confidenceScores.huggingface = 65;
    } else if (pattern.includes('downward') || pattern.includes('fall')) {
      signals.huggingface = 'SELL';
      confidenceScores.huggingface = 65;
    }
  }

  // Calculate weighted ensemble score
  let ensembleScore = 0;
  let totalWeight = 0;

  Object.keys(signals).forEach(model => {
    const signal = signals[model];
    const confidence = confidenceScores[model];
    const weight = weights[model];

    let signalValue = 0;
    if (signal.includes('BUY')) signalValue = 1;
    else if (signal.includes('SELL')) signalValue = -1;

    ensembleScore += signalValue * (confidence / 100) * weight;
    totalWeight += weight;
  });

  ensembleScore = ensembleScore / totalWeight;

  // Determine final ensemble signal
  let finalSignal = 'HOLD';
  let ensembleConfidence = Math.abs(ensembleScore) * 100;

  if (ensembleScore > 0.6) finalSignal = 'STRONG_BUY';
  else if (ensembleScore > 0.3) finalSignal = 'BUY';
  else if (ensembleScore > 0.1) finalSignal = 'WEAK_BUY';
  else if (ensembleScore < -0.6) finalSignal = 'STRONG_SELL';
  else if (ensembleScore < -0.3) finalSignal = 'SELL';
  else if (ensembleScore < -0.1) finalSignal = 'WEAK_SELL';

  // Calculate model agreement
  const buyCount = Object.values(signals).filter(s => s.includes('BUY')).length;
  const sellCount = Object.values(signals).filter(s => s.includes('SELL')).length;
  const holdCount = Object.values(signals).filter(s => s === 'HOLD').length;

  const agreement = Math.max(buyCount, sellCount, holdCount) / Object.keys(signals).length;
  ensembleConfidence = ensembleConfidence * agreement;

  const riskLevel = ensembleConfidence > 80 ? 'Low' : 
                   ensembleConfidence > 60 ? 'Medium' : 'High';

  const signalEmoji = {
    'STRONG_BUY': 'üöÄ',
    'BUY': 'üìà',
    'WEAK_BUY': 'üìä',
    'HOLD': '‚è∏Ô∏è',
    'WEAK_SELL': 'üìâ',
    'SELL': 'üìä',
    'STRONG_SELL': 'üí•'
  };

  return [{
    json: {
      ...technicalData,
      ensembleSignal: finalSignal,
      ensembleConfidence: Math.round(ensembleConfidence),
      ensembleScore: ensembleScore.toFixed(3),
      modelSignals: signals,
      modelConfidences: confidenceScores,
      modelWeights: weights,
      agreement: (agreement * 100).toFixed(1),
      signalDistribution: {
        buy: buyCount,
        sell: sellCount,
        hold: holdCount
      },
      riskLevel: riskLevel,
      emoji: signalEmoji[finalSignal] || '‚ö™',
      recommendation: ensembleConfidence > 70 ? 
        `Strong ${finalSignal} signal with ${Math.round(ensembleConfidence)}% confidence` :
        `Weak signal - Consider waiting for better setup`,
      timestamp: new Date().toISOString()
    }
  }];

} catch (error) {
  console.error('Ensemble analysis error:', error);
  return [{
    json: {
      error: 'Ensemble analysis failed: ' + error.message,
      ensembleSignal: 'HOLD',
      ensembleConfidence: 0,
      timestamp: new Date().toISOString()
    }
  }];
}

